{
  "bodyType": "text",
  "body": "Hello from CHES. The following is filled in by during the merge: {{ hello }}",
  "contexts": [
    {
      "to": ["{{email_to}}"],
      "context": {
        "orange": {
          "target": "delay"
        },
        "hello": "This message was sent with a 2 minute delay."
      },
      "delayTS": {{delay_ts}},
      "tag": "merge_{{session_tag}}"
    },
    {
      "to": ["{{email_to}}"],
      "context": {
        "orange": {
          "target": "immediate"
        },
        "hello": "This message was sent immediately."
      },
      "delayTS": 0,
      "tag": "merge_{{session_tag}}"
    }
  ],
  "encoding": "utf-8",
  "from": "{{email_to}}",
  "priority": "normal",
  "subject": "CHES Email Merge {{ orange.target }}",
  "attachments": [
    {
      "content": "",
      "contentType": "text/markdown",
      "encoding": "base64",
      "filename": "readme.md"
    }
  ]
}

